final List<dynamic> styles = [':host {\n  display: block;\n  font-family: inherit;\n  font-size: 15px;\n  line-height: 32px;\n  padding: 0 24px;\n  position: relative;\n  white-space: nowrap;\n  padding: 0 16px;\n  display: flex;\n  align-items: center;\n  transition: background;\n  color: rgba(0, 0, 0, 0.87);\n  cursor: pointer;\n}\n:host.disabled {\n  pointer-events: none;\n}\n:host ::ng-deep .material-list-item-primary {\n  color: rgba(0, 0, 0, 0.54);\n  width: 40px;\n}\n:host.disabled ::ng-deep .material-list-item-primary {\n  color: rgba(0, 0, 0, 0.38);\n}\n:host ::ng-deep .material-list-item-secondary {\n  color: rgba(0, 0, 0, 0.54);\n  margin-left: auto;\n}\n:host.disabled ::ng-deep .material-list-item-secondary {\n  color: rgba(0, 0, 0, 0.38);\n}\n:host ::ng-deep .submenu-icon {\n  transform: rotate(-90deg);\n}\n:host:hover, :host:focus, :host.active {\n  background: #f5f5f5;\n}\n:host:not(.multiselect).selected {\n  background: #eeeeee;\n}\n:host .selected-accent {\n  position: absolute;\n  top: 0;\n  left: 0;\n  bottom: 0;\n  width: 0;\n  border-left: 3px solid #9e9e9e;\n}\n:host material-checkbox {\n  margin: 0;\n}\n:host.disabled {\n  color: rgba(0, 0, 0, 0.38);\n  cursor: default;\n}\n:host.hidden {\n  display: none;\n}\n\n.check-container {\n  display: inline-block;\n  width: 40px;\n}\n\n.dynamic-item {\n  flex-grow: 1;\n  width: 100%;\n}\n\n:host-context([dir=rtl]) ::ng-deep .submenu-icon {\n  transform: rotate(90deg);\n}\n'];